mapper.select:
select choice.*,student.name as studentName, course.*, teacher.name as teacherName
from choice
left join student on choice.student_id = student.id
left join course on choice.course_id = course.id
left join teacher on choice.teacher_id = teacher.id

     and id= #{id}
     and choice.name like concat('%',#{name},'%')
     and choice.student_id= #{studentId}
     and choice.course_id= #{courseId}
     and choice.teacher_id= #{teacherId}

order by id desc
